SELECT n.name, kt.kind, k.keyword, COUNT(*) FROM name n JOIN cast_info ci ON n.id = ci.person_id JOIN title t ON ci.movie_id = t.id JOIN kind_type kt ON t.kind_id = kt.id JOIN movie_keyword mk ON t.id = mk.movie_id JOIN keyword k ON mk.keyword_id = k.id WHERE k.keyword IN ('crime', 'romance') AND kt.kind = 'movie' GROUP BY n.name, kt.kind, k.keyword ORDER BY COUNT(*) DESC LIMIT 20;