SELECT n.name, mi2.info AS country, COUNT(*) AS contributions FROM name n JOIN cast_info ci ON n.id = ci.person_id JOIN role_type rt ON ci.role_id = rt.id JOIN title t ON ci.movie_id = t.id JOIN movie_info mi2 ON t.id = mi2.movie_id JOIN info_type it2 ON mi2.info_type_id = it2.id WHERE rt.role = 'production designer' AND it2.id = 8 AND mi2.info IN ('Italy', 'USA', 'Japan') GROUP BY n.name, mi2.info ORDER BY contributions DESC LIMIT 10;