SELECT t.title, mii1.info AS rating, t.production_year FROM title t JOIN kind_type kt ON t.kind_id = kt.id JOIN movie_info_idx mii1 ON t.id = mii1.movie_id JOIN info_type it1 ON mii1.info_type_id = it1.id WHERE kt.kind = 'sci-fi' AND it1.id = 101 AND mii1.info ~ '^[0-9]+(\.[0-9]*)?$' ORDER BY rating::FLOAT DESC LIMIT 20;