SELECT d.name AS director, a.name AS actor, COUNT(*) AS collaborations FROM cast_info ci1 JOIN cast_info ci2 ON ci1.movie_id = ci2.movie_id AND ci1.person_id != ci2.person_id JOIN name d ON ci1.person_id = d.id JOIN name a ON ci2.person_id = a.id JOIN role_type rt1 ON ci1.role_id = rt1.id JOIN role_type rt2 ON ci2.role_id = rt2.id WHERE rt1.role = 'director' AND rt2.role = 'actor' GROUP BY director, actor ORDER BY collaborations DESC LIMIT 10;